<template>
  <button
    :class="`scribble__button scribble__button--${variant}`"
    :style="`background-image: url(${backgroundSrc})`"
  >
    <span v-if="text" class="scribble__text"
      ><span class="scribble__text--inner">{{ text }}</span></span
    >
    <div v-else class="scribble__button--children scribble__text">
      <slot></slot>
    </div>
  </button>
</template>
<script>
import { defineComponent } from 'vue'

export default defineComponent({
  name: 'BikeTagButton',
  props: {
    text: {
      type: String,
      default: '',
    },
    variant: {
      type: String,
      default: 'light',
    },
  },
  computed: {
    backgroundSrc() {
      switch (this.variant) {
        case 'medium':
          return 'data:image/svg+xml;base64,'
        case 'underline-reverse':
        case 'underline':
          return 'data:image/svg+xml;base64,'
        case 'circle-clean':
          return 'data:image/svg+xml;base64,'
        case 'circle':
          return 'data:image/svg+xml;base64,'
        case 'bold':
          return 'data:image/svg+xml;base64,'
        case 'light':
        default:
          return 'data:image/svg+xml;base64,'
      }
    },
  },
})
</script>
